
// This file is deprecated.
// The new service worker is 'service-worker.js' generated by Vite.
// This script acts as a bridge to unregister itself and register the new one.

self.addEventListener('install', () => {
  self.skipWaiting();
});

self.addEventListener('activate', (event) => {
  event.waitUntil(
    self.registration.unregister().then(() => {
      // Force clients to reload/re-register the new SW
      return self.clients.matchAll();
    }).then((clients) => {
      clients.forEach(client => client.navigate(client.url));
    })
  );
});
